'',
'      apex_javascript.add_library(',
'         --p_name      => ''apex_super_lov.min'',',
'         p_name      => ''apex_super_lov'',',
'         p_directory => p_plugin.file_prefix,',
'         p_version   => NULL',
'      );',
'',
'      l_onload_code := ''apex.jQuery("input#'' || p_item.name || ''").apex_super_lov({'' || l_crlf',
