,p_process_name=>'HEADER_PROCESS'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'L_msg VARCHAR2(2000);',
'VESHIS_VES_SEQ_IDs VESSEL_TRIPS.VESHIS_VES_SEQ_ID%type;',
'VESHIS_SEQ_CHILDs  VESSEL_TRIPS.VESHIS_SEQ_CHILD%type;',
'evt_seq_ids events1.seq_id%type;',
'SAMP_SEQ_IDs samples.seq_id%type;',
'',
'CURSOR VScur(VES_SEQ_IDs number,',
'DATE_ONBOARDs VARCHAR2,',
'EQPTYP_SEQ_IDs number) IS SELECT  VES_SEQ_ID,SEQ_CHILD,EQPTYP_SEQ_ID FROM VESSEL_EQUIPMENTS  VE ',
'where VE.VES_SEQ_ID=to_number(VES_SEQ_IDs) and VE.EQPTYP_SEQ_ID=EQPTYP_SEQ_IDs ',
'AND VE.DATE_ONBOARD=(select max(VE1.DATE_ONBOARD) from VESSEL_EQUIPMENTS VE1',
'WHERE VE1.EQPTYP_SEQ_ID=EQPTYP_SEQ_IDs and VE1.VES_SEQ_ID=VES_SEQ_IDs AND VE1.DATE_ONBOARD<=to_date(DATE_ONBOARDs,''MM/DD/YYYY''));',
'VSvar VScur%rowtype;',
'',
'BEGIN',
'L_msg:=NULL;',
'select VESHIS_VES_SEQ_ID,VESHIS_SEQ_CHILD  into VESHIS_VES_SEQ_IDs,VESHIS_SEQ_CHILDs from vessel_trips VT where vt.trp_seq_id=to_number(:P51_VESSEL_TRIP); ',
':P51_VESSEL_ID:=VESHIS_VES_SEQ_IDs;',
':P51_VESSEL_CHILD:=VESHIS_SEQ_CHILDs;',
'',
' /*START TO GET GEAR USAGE*/',
'OPEN VScur(to_number(veshis_VES_SEQ_IDs),to_char(:P51_SAMPLED_DATE),to_number(:P51_GEAR));',
'FETCH VScur into VSvar;',
'  if VScur%NOTFOUND then',
' L_msg:=L_msg||''<tr><center><span style="color:RED">gear not found.<span></center></tr>'';',
'  ',
'else',
'  INSERT INTO EVENTS1(DATE_BEGIN,VESTRP_VESHIS_VES_SEQ_ID,VESTRP_VESHIS_SEQ_CHILD,VESTRP_TRP_SEQ_ID,PGM_SEQ_ID,EVTTYP_CODE,NOTE)',
