'insert into EVENT_ASSOCIATIONS(evt_seq_id,PERSNL_SEQ_ID,EVTATYP_CODE)',
'values(evt_seq_ids,to_number(:P51_SAMPLER),''MEAS'');',
'insert into event_locations(evt_seq_id,loc_seq_id)values(EVT_SEQ_IDS,to_number(:P51_PORT));',
'INSERT INTO SAMPLES(EVT_SEQ_ID,SAMPTYP_CODE,pgm_seq_id)',
'values(evt_seq_ids,''LFLF'',''19'') returning seq_id into SAMP_SEQ_IDs;',
'INSERT into SAMPLE_SPECIES(SAMP_SEQ_ID,weight_best,SPEC_CODE,UOM_CODE_WT)',
'values(SAMP_SEQ_IDs,:P51_TOTAL_WT,5,''LB'');',
'end if;',
